declare const _default: "\n<div data-type='table' className={styles['div-table']} {...attributes} data-row={attrs.rows} data-col={attrs.cols} data-colwidth={attrs.colWidths}>\n    <TableColumnDnd element={props.element} tablePath={slatePath}></TableColumnDnd>\n    {children}\n    <TableRowAdd tablePath={slatePath}></TableRowAdd>\n</div>\n";
export default _default;
